const os = require('os')

const externalIPv4 = ({ family, internal }) => family === 'IPv4' && !internal
const getIP = () => {
  const ifaces = os.networkInterfaces()
  for (const i in ifaces) {
    const iface = ifaces[i].find(externalIPv4)
    if (iface) return iface.address
  }
  return 'localhost'
}

module.exports = getIP
